mapper.select:
SELECT B.ID FROM BOOK B WHERE B.BOOK_CASE_ID IN
#{bookCaseId}
AND B.STATE = 'NORMAL'

mapper.update:
UPDATE BOOK SET STATE = 'SHOULD_BE_RETRIEVED' WHERE ID IN
#{bookId}
AND STATE = 'NORMAL'

UPDATE BOOK SET DELETED_AT = SYSTIMESTAMP WHERE ID IN
#{bookId}
AND STATE = 'SHOULD_BE_RETRIEVED' AND DELETED_AT IS NULL

mapper.select:
SELECT COUNT(*) FROM BOOK
WHERE ID IN

    #{bookId}

AND STATE = #{state}
AND DELETED_AT IS NULL
