mapper.select:
SELECT COUNT(*)
FROM BOOK_SALE_RECORD BSR
JOIN BOOK B ON BSR.ID = B.ID
WHERE BSR.ID IN

    #{id}

AND B.BOOK_OWNER_ID = #{bookOwnerId}

SELECT COUNT(*) FROM BOOK_SALE_RECORD WHERE ID IN
#{id}
AND BOOK_OWNER_SETTLEMENT_ID IS NOT NULL

mapper.update:
UPDATE BOOK_SALE_RECORD
SET BOOK_OWNER_SETTLEMENT_ID = #{settlementId}
WHERE ID IN

    #{id}
